css:
@media all {
  .print-format {
       /*216mm or 21.6cm or 8.5in (inches)*/
       max-width: 21cm;
       /*330mm or 33.0cm or 13.0in (inches)*/
       min-height: 29.7cm;
       /* Margin from edge of page (padding) 0.0cm*/
       padding: 0.0cm;
       margin: 0.1cm 0.1cm 0.1cm 0.1cm;
       background-color: rgba(255,255,255,1.0);
       box-shadow: 0px 0px 0px rgba(0,0,0,0);
       line-height: 2;
  }
.print-format.landscape {
      max-width: 29.7cm;
      width: 29.7cm;
      line-height: 0.8;
  }
.print-format.portrait {
      max-width: 21cm;
      width: 21cm;
      line-height: 0.8;
  }
}
@page {
    size: 21cm 29.7cm portrait;
    line-height: 0.8;
}
.marginfree {
    margin-right: 0px;
    margin-left: 0px;
}
